const wm = this.getWrexMods();
const fs = wm.require("fs");
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const memberjoin = tempVars('memberjoin');
let embed = new DiscordJS.RichEmbed();

(async () => {
if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.member-join`);
    if (log_system === "enabled") {
        const memS = await server.fetchMembers();
        embed.setTitle('Member Joined');
        embed.setColor('#3dd0f4');
        embed.setAuthor(memberjoin.user.tag, memberjoin.user.displayAvatarURL);
        embed.setThumbnail(memberjoin.user.displayAvatarURL);
        embed.addField('User', memberjoin, true);
        embed.addField('Member Count', server.members.size, true);
        embed.setFooter(`ID: ${memberjoin.user.id}`);
        embed.setTimestamp();
        server.channels.get(log_chan).send(embed);
        db.add(`${server.id}logs.member_joined`, 1);
        db.add(`${server.id}logs.all`, 1);
    }
}
})();

