const wm = this.getWrexMods();
const fs = wm.require("fs");
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const msgdel = tempVars('msgdel');
const delby = tempVars("delby");
let embed = new DiscordJS.RichEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.message-deleted`);
    if (log_system === "enabled") {
        if (msgdel.author.bot == false) {
            embed.setTitle('Message Deleted');
            embed.setURL(msgdel.url);
            embed.setAuthor(msgdel.author.tag, msgdel.author.displayAvatarURL);
            embed.setThumbnail(msgdel.author.displayAvatarURL);
            embed.addField('Deleted Text', (msgdel.content.length <= 1024) ? msgdel.content : `${msgdel.content.substring(0, 1020)}...`, true);
            embed.addField('Channel', msgdel.channel, true);
            embed.addField('Message Author', `${msgdel.author} (${msgdel.author.tag})`, true);
            (delby) ? (msgdel.author !== delby) ? embed.addField('Deleted By', delby, true): '' : '';
            (msgdel.mentions.users.size === 0) ? embed.addField('Mentioned Users', 'None', true): embed.addField('Mentioned Users', `Mentioned Member Count: ${msgdel.mentions.users.array().length} \n Mentioned Users List: \n ${msgdel.mentions.users.array()}`, true);
            embed.setTimestamp();
            server.channels.get(log_chan).send(embed);
            db.add(`${server.id}logs.message_deleted`, 1);
            db.add(`${server.id}logs.all`, 1);
        }
    }
}

